public class HxbDecodeUtil {
    private static String HXB_BANK_CERT_PUB = "";
//    String jsonResData = JXMConvertUtil.XmlConvertJson(base64DecodeResData);
public static String getHXBEntryDataStr(String resData) throws Exception {
        if (org.springframework.util.StringUtils.isEmpty(HXB_BANK_CERT_PUB)) {
            throw new Exception("未获取到华夏银行解密证书信息");
        }
        byte[] hxbCertPub = Base64.decode(HXB_BANK_CERT_PUB);
        return SecurityUtil.Base64Decode(RsaCodingUtil.decryptByPubCerFile(resData, new ByteArrayInputStream(hxbCertPub)));
    }
}
